# Access Policy definierenSet-AzKeyVaultAccessPolicy `    -VaultName 'myKeyVault' `    -ResourceGroupName 'myRG' `    -EnabledForDiskEncryption# Disk Encryption Extension an die VM hängenSet-AzVMDiskEncryptionExtension `    -ResourceGroupName 'myRG' `    -VMName 'myVM' `    -VolumeType All `    -DiskEncryptionKeyVaultId 'keyvault.ResourceId' `    -DiskEncryptionKeyVaultUrl 'keyvault.VaultUri' `    -SkipVmBackup# Encryption Status abfragenGet-AzVMDiskEncryptionStatus `    -ResourceGroupName 'myRG' `    -VMName 'myVM'